import React from 'react'
import { Link, useHistory } from 'react-router-dom';
import { deleteDeck } from '../../utils/api/index';

function DeckList({ id, name, description, cards, updateDecks }) {
  const history = useHistory();

  const handleDelete = async () => {
    const itemToDelete = window.confirm("Delete this deck? \n \n You will not be able to recover it.");
    if(itemToDelete) {
      await deleteDeck(id);
      updateDecks(-1);
      history.push('/');
    }
  }

  return (
    <div className='card'>
      <div className="card-body">
        <div style={{display: 'flex', justifyContent: 'space-between'}}>
          <h3 className="card-title">{name}</h3>
          <p>{cards.length} Cards</p>
        </div>
        <p className="card-text">{description}</p>
        <div style={{display: 'flex', justifyContent: 'space-between'}}>
          <div>
            <Link to={`decks/${id}`} className="btn btn-secondary" style={{marginRight: '10px'}}>View</Link>
            <Link to={`decks/${id}/study`} className='btn btn-primary'>Study</Link>
          </div>
          <div>
            <button className="btn btn-danger">Delete</button>
          </div>
        </div>
      </div>
      
    </div>
  )
}

export default DeckList
