import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { listCards } from '../../utils/api/index';


function ViewDeck( { deckList }) {
  const [cardList, setCardList] = useState([]);
  const { deckId } = useParams();


  useEffect(() => {
    const abortController = new AbortController();
    const loadCards = async () => {
      const cards = await listCards(deckId, abortController.signal);
      setCardList(() => cards);
    }
    loadCards();
    return () => abortController.abort();
  }, [deckId]);
  
  return (
    <div>
      {cardList.map(card => {
        return (
          <div className='card' style={{display: 'flex', flexDirection: 'row', justifyContent: 'center'}}>
            <div style={{flex: '1', margin: '15px'}}>
              {card.front}
            </div>
            <div style={{flex: '1', margin: '15px'}}>
              {card.back}
              <div style={{alignContent: 'right'}}>
                <button className='btn btn-secondary'>Edit</button>
                <button className='btn btn-danger'>Delete</button>
              </div>
            </div>
          </div>
        )
      })}
    </div>
  )
}

export default ViewDeck
