import React from 'react'

function CardList( {cardList} ) {
  return (
    <div>
            {cardList.map((card, index)=> {
        return (
          <div key={index} className='card' style={{display: 'flex', flexDirection: 'row', justifyContent: 'center'}}>
            <div style={{flex: '1', margin: '15px'}}>
              {card.front}
            </div>
            <div style={{flex: '1', margin: '15px'}}>
              {card.back}
              <div style={{marginLeft: '40px', marginTop: '15px'}}>
                <button className='btn btn-secondary' style={{marginRight: '8px'}}>Edit</button>
                <button className='btn btn-danger'>Delete</button>
              </div>
            </div>
          </div>
        )
      })}
    </div>
  )
}

export default CardList
